mapper.select:
SELECT
    DISTINCT a.member_uid
FROM orders a
JOIN order_details b ON a.order_uid = b.order_uid
WHERE b.book_id = #{ bookId }
AND a.member_uid = #{ memberUid }
AND a.order_status_id = 1
AND a.member_uid NOT IN
    (
        SELECT s1.member_uid
        FROM reviews s1
        WHERE s1.book_id = #{ bookId }
    )

SELECT DISTINCT b.book_id, b.book_name, b.image_url, b.price, a.author_id FROM books b JOIN book_authors ba ON b.book_id = ba.book_id JOIN authors a ON ba.author_id = a.author_id WHERE b.is_deleted = false
AND b.book_id != #{bookId}
AND a.author_id IN
#{id}
ORDER BY b.created_at DESC LIMIT #{limit}
